import React, { FC, useContext, useState } from "react";

import {
	Typography,
	Button,
	TextField,
	Stepper,
	Step,
	StepLabel,
	StepContent,
	Avatar,
} from "@material-ui/core";
import { makeStyles, createStyles } from "@material-ui/core/styles";

import logo from "../../img/logo.png";

const useStyles = makeStyles((theme) =>
	createStyles({
		root: {
			width: "100%",
		},
		avatar: {
			height: theme.spacing(10),
			width: theme.spacing(10),
			marginLeft: "auto",
			marginRight: "auto",
		},
		buttonContainer: {
			marginTop: theme.spacing(3),
		},
		nextButton: {
			marginLeft: theme.spacing(2),
			backgroundColor: "#007c89",
			color: "white",
		},
	})
);

function getSteps() {
	return ["PH Level", "Ammonia Level", "Nitrite Level", "Nitrate Level"];
}

function getStepContent(step: number) {
	switch (step) {
		case 0:
			return `Enter Your pH Reading In The Input Box Below`;
		case 1:
			return `Enter Your Ammonia Reading In The Input Box Below`;
		case 2:
			return `Enter Your Nitrite Reading In The Input Box Below`;
		case 3:
			return `Enter Your Nitrate Reading In The Input Box Below`;
		default:
			return "Unknown Step!";
	}
}

const WaterParam: FC = () => {
	const classes = useStyles();
	const [activeStep, setActiveStep] = useState(0);
	const steps = getSteps();

	const nextStep = () => {
		setActiveStep((prevActiveStep) => prevActiveStep + 1);
	};

	const prevStep = () => {
		setActiveStep((prevActiveStep) => prevActiveStep - 1);
	};

	const resetStepper = () => {
		setActiveStep(0);
	};

	return (
		<div className={classes.root}>
			<Avatar src={logo} variant="circle" className={classes.avatar} />
			<Stepper activeStep={activeStep} orientation="vertical">
				{steps.map((label, index) => (
					<Step key={label}>
						<StepLabel>{label}</StepLabel>
						<StepContent>
							<Typography>{getStepContent(index)}</Typography>
							<div className={classes.buttonContainer}>
								<div>
									<Button
										variant="contained"
										size="small"
										style={
											activeStep === 0
												? { display: "none" }
												: { backgroundColor: "#df3106", color: "white" }
										}
										onClick={prevStep}
									>
										Back
									</Button>
									<Button
										variant="contained"
										size="small"
										style={
											activeStep === steps.length - 1 ? { display: "none" } : ""
										}
										onClick={nextStep}
										className={classes.nextButton}
									>
										Next
									</Button>
								</div>
							</div>
						</StepContent>
					</Step>
				))}
			</Stepper>
		</div>
	);
};

export default WaterParam;
